import React, { useState, useEffect } from 'react';
import './Jugadores.css';
import defaultPlayerImage from '../assets/default-player.png'; // Añadir imagen predeterminada del jugador
import clubLogo from '../assets/club-logo.png'; // Añadir imagen predeterminada del club

function Jugadores() {
  const [players, setPlayers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Hacer la solicitud al backend para obtener los jugadores
    fetch(`${process.env.REACT_APP_API_URL}/chess_players`)
      .then(response => response.json())
      .then(data => setPlayers(data))
      .catch(error => console.error('Error al obtener los jugadores:', error));
  }, []);

  const filteredPlayers = players.filter(player =>
    `${player.first_name} ${player.last_name}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="jugadores-container">
      <h2>Jugadores</h2>
      <input
        type="text"
        placeholder="Buscar jugador"
        className="search-input"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div className="players-list">
        {filteredPlayers.map(player => (
          <div key={player.tablero} className="player-card">
            <div className="player-summary">
              <img src={defaultPlayerImage} alt="Jugador" className="player-image" />
              <div className="player-details">
                <h3>{`${player.first_name} ${player.last_name}`}</h3>
                <p>Club: {player.club}</p>
                <p>División: {player.division}</p>
                <p>ELO FIDE: {player.elo_fide || '-'}</p>
                <p>Puntos totales: 0</p>
                <p>Partidos jugados: 11</p>
              </div>
              <img src={clubLogo} alt="Club" className="club-logo" />
            </div>
            <div className="player-stats">
              <span className="division-tag">{player.division === 'División de Honor' ? 'DH' : player.division}</span>
              <span className="total-points">0</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Jugadores;
