// src/pages/Registro.js
<button className="login-btn" onClick={() => navigate('/home')}>Ya tengo cuenta</button>

import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css'; 
import logo from '../assets/logo.png'; // Importamos el logo

function Registro() {
  const navigate = useNavigate();

  const handleRegister = () => {
    // Lógica de registro si la tienes (API, validaciones, etc.)
    navigate('/home'); // Redirige al usuario a /home después de registrarse
  };

  return (
    <div className="home-container">
      <img src={logo} alt="Logo" className="logo" /> {/* Aquí cargamos el logo PNG */}
      <div className="content">
        <h2>Bienvenido a My Chess Fantasy</h2>
        <button className="create-account-btn" onClick={handleRegister}>Crear Cuenta</button>
        <button className="login-btn">Ya tengo cuenta</button> {/* Nuevo botón de diseño moderno */}
      </div>
    </div>
  );
}

export default Registro;
