import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Registro.css';
import logo from '../assets/logo.png'; // Importamos el logo

function Registro() {
  const navigate = useNavigate();
  const [showCreateAccount, setShowCreateAccount] = useState(false); // Controla si mostrar el formulario de crear cuenta
  const [showLogin, setShowLogin] = useState(false); // Controla si mostrar el formulario de login

  const handleCreateAccount = () => {
    setShowCreateAccount(true);
    setShowLogin(false); // Oculta el formulario de login si estaba visible
  };

  const handleLogin = () => {
    setShowCreateAccount(false);
    setShowLogin(true); // Oculta el formulario de crear cuenta si estaba visible
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    // Lógica de registro (API, validaciones, etc.)
    navigate('/home'); // Redirige al usuario después de registrarse
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // Lógica de inicio de sesión
    navigate('/home'); // Redirige al usuario después de iniciar sesión
  };

  return (
    <div className="home-container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="content">
        <h2>GALICHESS FANTASY</h2>
        {/* Botones principales */}
        <button className="create-account-btn" onClick={handleCreateAccount}>Crear Cuenta</button>
        <button className="login-btn" onClick={handleLogin}>Ya tengo cuenta</button>

        {/* Formulario de Crear Cuenta */}
        {showCreateAccount && (
          <form onSubmit={handleRegisterSubmit} className="form-container">
            <input type="email" placeholder="Email" required className="input-field" />
            <input type="password" placeholder="Contraseña" required className="input-field" />
            <input type="password" placeholder="Confirmar contraseña" required className="input-field" />
            <button type="submit" className="submit-btn">Regístrate</button>
          </form>
        )}

        {/* Formulario de Login */}
        {showLogin && (
          <form onSubmit={handleLoginSubmit} className="form-container">
            <input type="email" placeholder="Email" required className="input-field" />
            <input type="password" placeholder="Contraseña" required className="input-field" />
            <button type="submit" className="submit-btn">Iniciar Sesión</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Registro;
