import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Registro.css';
import logo from '../assets/logo.png'; // Importamos el logo

function Registro() {
  const navigate = useNavigate();
  const [activeForm, setActiveForm] = useState(null); // Controla qué formulario está activo

  const handleCreateAccountClick = () => {
    setActiveForm(activeForm === 'register' ? null : 'register'); // Alterna el formulario de registro
  };

  const handleLoginClick = () => {
    setActiveForm(activeForm === 'login' ? null : 'login'); // Alterna el formulario de login
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    navigate('/home'); // Redirige al usuario después de registrarse
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    navigate('/home'); // Redirige al usuario después de iniciar sesión
  };

  return (
    <div className="home-container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="content">
        <h2>GALICHESS FANTASY</h2>

        {/* Botón Crear Cuenta */}
        <button className={`toggle-btn ${activeForm === 'register' ? 'active' : ''}`} onClick={handleCreateAccountClick}>
          Crear Cuenta
        </button>

        {/* Formulario de Crear Cuenta */}
        {activeForm === 'register' && (
          <form onSubmit={handleRegisterSubmit} className="form-container">
            <input type="email" placeholder="Email" required className="input-field" />
            <input type="password" placeholder="Contraseña" required className="input-field" />
            <input type="password" placeholder="Confirmar contraseña" required className="input-field" />
            <button type="submit" className="submit-btn">Regístrate</button>
          </form>
        )}

        {/* Botón Ya tengo cuenta */}
        <button className={`toggle-btn ${activeForm === 'login' ? 'active' : 'inactive'}`} onClick={handleLoginClick}>
          Ya tengo cuenta
        </button>

        {/* Formulario de Login */}
        {activeForm === 'login' && (
          <form onSubmit={handleLoginSubmit} className="form-container">
            <input type="email" placeholder="Email" required className="input-field" />
            <input type="password" placeholder="Contraseña" required className="input-field" />
            <button type="submit" className="submit-btn">Iniciar Sesión</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Registro;
