import React, { useState, useEffect } from 'react';
import './Jugadores.css'; // Asegúrate de crear el archivo CSS correspondiente para estilizar

function Jugadores() {
  const [players, setPlayers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Petición a la API para obtener los jugadores dinámicamente
    fetch(`${process.env.REACT_APP_API_URL}/chess_players`)
      .then(response => response.json())
      .then(data => setPlayers(data))
      .catch(error => console.error('Error al obtener los jugadores:', error));
  }, []);

  const filteredPlayers = players.filter(player =>
    player.first_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    player.last_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="jugadores-container">
      <h2>Jugadores Primera División</h2>
      <input
        type="text"
        placeholder="Buscar jugador"
        className="search-input"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div className="players-list">
        {filteredPlayers.map(player => (
          <div key={player.id} className="player-card">
            <div className="player-info">
              <img src={player.image_url} alt={player.first_name} className="player-image" />
              <h3>{player.first_name} {player.last_name}</h3>
              <p>Club: {player.club}</p>
              <p>División: {player.division}</p>
              <p>ELO FIDE: {player.elo_fide}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Jugadores;
