import React, { useState, useEffect } from 'react';
import './Jugadores.css'; // Asegúrate de crear el archivo CSS correspondiente para estilizar

function Jugadores() {
  const [players] = useState([
    { id: 1, name: 'Magnus Carlsen', team: 'Equipo A', value: '20,000,000 €', position: 'GM', points: 101 },
    { id: 2, name: 'Fabiano Caruana', team: 'Equipo B', value: '18,000,000 €', position: 'IM', points: 98 },
    // Añade más jugadores aquí
  ]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Aquí podrías hacer una petición a la API para obtener los jugadores dinámicamente
    // fetch(`${process.env.REACT_APP_API_URL}/players`)
    //   .then(response => response.json())
    //   .then(data => setPlayers(data))
    //   .catch(error => console.error('Error al obtener los jugadores:', error));
  }, []);

  const filteredPlayers = players.filter(player =>
    player.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="jugadores-container">
      <h2>Jugadores</h2>
      <input
        type="text"
        placeholder="Buscar jugador"
        className="search-input"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div className="players-list">
        {filteredPlayers.map(player => (
          <div key={player.id} className="player-card">
            <div className="player-info">
              <h3>{player.name}</h3>
              <p>Equipo: {player.team}</p>
              <p>Valor: {player.value}</p>
              <p>Puntos: {player.points}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Jugadores;
